    position: absolute;
    overflow: auto;
    top: 0;
    left: 0;
  }

  .p-autocomplete-items {
    margin: 0;
    padding: 0;
    list-style-type: none;
  }

  .p-autocomplete-item {
    cursor: pointer;
    white-space: nowrap;
    position: relative;
    overflow: hidden;
  }

  .p-autocomplete-multiple-container {
