  }

  /* Fluid */
  .p-fluid .p-calendar {
    display: flex;
  }

  .p-fluid .p-calendar .p-inputtext {
    width: 1%;
  }

  /* Datepicker */
  .p-calendar .p-datepicker {
    min-width: 100%;
  }

  .p-datepicker {
    width: auto;
  }

  .p-datepicker-inline {
    display: inline-block;
    overflow-x: auto;
  }

  /* Header */
  .p-datepicker-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  .p-datepicker-header .p-datepicker-title {
    margin: 0 auto;
  }

  .p-datepicker-prev,
.p-datepicker-next {
    cursor: pointer;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    position: relative;
  }

  /* Multiple Month DatePicker */
