    display: flex;
  }

  .p-datepicker-multiple-month .p-datepicker-group-container .p-datepicker-group {
    flex: 1 1 auto;
  }

  /* DatePicker Table */
  .p-datepicker table {
    width: 100%;
    border-collapse: collapse;
  }

  .p-datepicker td > span {
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    margin: 0 auto;
    overflow: hidden;
    position: relative;
  }

  /* Month Picker */
  .p-monthpicker-month {
    width: 33.3%;
    display: inline-flex;
    align-items: center;
