html,
body {
  width: 100%;
  overflow-x: hidden;
  background-color: #f9fbfd;
  padding: 0;
}

* {
  box-sizing: border-box ! important;
}

.hidden {
  visibility: hidden;
  opacity: 0;
  height: 0;
  overflow: hidden;
  transition: opacity 0.3s ease;
}

.visible {
  visibility: visible;
  opacity: 1;
  height: auto;
  transition: opacity 0.3s ease;
}

tabltable {
  width: 100% !important;
  cursor: pointer;
  table-layout: auto !important;
  overflow-x: auto; /* Mantém a rolagem horizontal */
}

table th,
table td {
  border: 1px solid #ddd;
  padding: 4px !important;
  text-align: center !important;
  font-size: 12px !important;
  color: #ffffff;
  letter-spacing: 1px;
  word-wrap: break-word;
  word-break: break-all; /* Quebra palavras longas em qualquer ponto */
  white-space: normal;
  min-width: 60px; /* Reduz a largura mínima */
  overflow-wrap: break-word;
}

/* Media Queries para telas menores */
@media (max-width: 768px) {
  table th,
  table td {
    font-size: 10px !important; /* Reduz o tamanho da fonte */
    padding: 8px !important; /* Reduz o padding */
  }

  /* Oculta colunas menos importantes */
  table td:nth-child(n + 17),
  table th:nth-child(n + 17) {
    display: none;
  }
}

@media (max-width: 480px) {
  table th,
  table td {
    font-size: 10px !important; /* Reduz ainda mais o tamanho da fonte */
    padding: 5px !important; /* Reduz ainda mais o padding */
  }

  /* Oculta mais colunas */
  table td:nth-child(n + 11),
  table th:nth-child(n + 11) {
    display: none;
  }
}

table th {
  position: sticky;
  top: 0;
  background-color: #3e3d3d;
  color: #f1f1ff;
  z-index: 1;
  box-shadow: 0 2px 2px -1px rgba(0, 0, 0, 0.4);
  width: auto;
}

.table,
#graficos {
